import {Router} from "@angular/router";
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-index',
  templateUrl: './index.component.html',
  styleUrls: ['./index.component.css']
})
export class IndexComponent {
  constructor(private router:Router) {
  }

  indexBackground:string ="assets/img/index.jpg"
  BgSize:string="20px";

  GoHome(){
    this.router.navigate(['/home']);
  }





}

<html lang="en" >
<head>
  <meta charset="UTF-8">
  <title>Login Page in HTML with CSS Code Example</title>

</head>
<body >

<div class="UGimg">
<img src="/assets/img/UG.png" >

</div>
<div class="bottom">

  <button class="button_1" style="vertical-align:middle" ><span>Join Now</span></button>
  <button (click)="GoHome()"  class="button_1" style="vertical-align:middle" ><span>Login</span></button>
</div>


</body>



</html>
